import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import './PersonalFinance.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const incomeCategories = [
    "Salary – Wages earned from full-time or part-time employment",
    "Hourly Wages – Income earned based on hours worked",
    "Self-Employment Income – Earnings from freelancing, consulting, or running a business",
    "Commission – Earnings based on sales performance (e.g., real estate agents, salespeople)",
    "Bonuses – Extra earnings from work performance or company profits",
    "Investment Income – Dividends from stocks, interest from bonds",
    "Rental Income – Earnings from renting out property",
    "Royalties – Income from intellectual property, books, music, patents",
    "Affiliate Marketing Income – Earnings from promoting products/services",
    "Advertising Revenue – Income from YouTube, blogs, or social media monetization",
    "Unemployment Benefits – Financial aid for those out of work",
    "Disability Benefits – Government support for individuals with disabilities",
    "Social Security/Pension – Retirement benefits",
    "Child Support/Alimony – Payments received from a former spouse",
    "Scholarships/Grants – Education funding that doesn’t require repayment",
    "Side Hustles – Income from gig work (Uber, Airbnb, online selling, etc.)",
    "Lottery & Gambling Winnings – Money won from betting, casinos, or lotteries",
    "Inheritances & Gifts – Money received from family or friends",
    "Tax Refunds – Money returned by the government after tax filing"
];

const expenseCategories = [
    "Housing – Rent, mortgage, property taxes, home maintenance",
    "Utilities – Electricity, water, gas, internet, phone bills",
    "Groceries – Food, household essentials",
    "Transportation – Fuel, public transport, car payments, maintenance, insurance",
    "Healthcare – Insurance, medical bills, prescriptions, dental, vision care",
    "Debt Payments – Loan repayments, credit card bills",
    "Insurance – Health, life, car, home, disability insurance",
    "Discretionary Expenses (Wants) – Dining Out, Entertainment, Shopping, Travel & Vacations, Subscriptions, Gifts & Donations",
    "Savings & Investments – Emergency Fund, Retirement Savings, Education"
];

const PersonalFinance = ({ darkMode }) => {
    const [income, setIncome] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [selectedIncomeCategory, setSelectedIncomeCategory] = useState('');
    const [incomeAmount, setIncomeAmount] = useState('');
    const [selectedExpenseCategory, setSelectedExpenseCategory] = useState('');
    const [expenseAmount, setExpenseAmount] = useState('');

    const addIncome = () => {
        if (selectedIncomeCategory && incomeAmount) {
            setIncome([...income, { category: selectedIncomeCategory, amount: parseFloat(incomeAmount) }]);
            setSelectedIncomeCategory('');
            setIncomeAmount('');
        }
    };

    const addExpense = () => {
        if (selectedExpenseCategory && expenseAmount) {
            setExpenses([...expenses, { category: selectedExpenseCategory, amount: parseFloat(expenseAmount) }]);
            setSelectedExpenseCategory('');
            setExpenseAmount('');
        }
    };

    const editIncome = (index) => {
        const newAmount = prompt("Enter new amount:", income[index].amount);
        if (newAmount) {
            const updatedIncome = [...income];
            updatedIncome[index].amount = parseFloat(newAmount);
            setIncome(updatedIncome);
        }
    };

    const removeIncome = (index) => {
        const updatedIncome = income.filter((_, i) => i !== index);
        setIncome(updatedIncome);
    };

    const editExpense = (index) => {
        const newAmount = prompt("Enter new amount:", expenses[index].amount);
        if (newAmount) {
            const updatedExpenses = [...expenses];
            updatedExpenses[index].amount = parseFloat(newAmount);
            setExpenses(updatedExpenses);
        }
    };

    const removeExpense = (index) => {
        const updatedExpenses = expenses.filter((_, i) => i !== index);
        setExpenses(updatedExpenses);
    };

    const totalIncome = income.reduce((acc, curr) => acc + curr.amount, 0);
    const totalExpenses = expenses.reduce((acc, curr) => acc + curr.amount, 0);
    const balance = totalIncome - totalExpenses;

    const data = {
        labels: ['Income', 'Expenses'],
        datasets: [
            {
                label: 'Amount (£)',
                data: [totalIncome, totalExpenses],
                backgroundColor: ['#4CAF50', '#FF6384'],
            },
        ],
    };

    return (
        <div className={`personal-finance ${darkMode ? 'dark-mode' : ''}`}>
            <div className="finance-container">
                <div className="finance-form">
                    <div>
                        <h3>Income</h3>
                        <select 
                            value={selectedIncomeCategory} 
                            onChange={(e) => setSelectedIncomeCategory(e.target.value)}
                            className="finance-select"
                        >
                            <option value="">Select Income Category</option>
                            {incomeCategories.map((category, index) => (
                                <option key={index} value={category}>{category}</option>
                            ))}
                        </select>
                        <input 
                            type="number" 
                            value={incomeAmount} 
                            onChange={(e) => setIncomeAmount(e.target.value)} 
                            placeholder="Income Amount (£)" 
                            className="finance-input"
                        />
                        <button onClick={addIncome} className="finance-button">Add Income</button>
                        <ul>
                            {income.map((inc, index) => (
                                <li key={index}>
                                    {inc.category}: £{inc.amount}
                                    <button onClick={() => editIncome(index)} className="edit-button">Edit</button>
                                    <button onClick={() => removeIncome(index)} className="remove-button">Remove</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                    <div>
                        <h3>Expenses</h3>
                        <select 
                            value={selectedExpenseCategory} 
                            onChange={(e) => setSelectedExpenseCategory(e.target.value)}
                            className="finance-select"
                        >
                            <option value="">Select Expense Category</option>
                            {expenseCategories.map((category, index) => (
                                <option key={index} value={category}>{category}</option>
                            ))}
                        </select>
                        <input 
                            type="number" 
                            value={expenseAmount} 
                            onChange={(e) => setExpenseAmount(e.target.value)} 
                            placeholder="Expense Amount (£)" 
                            className="finance-input"
                        />
                        <button onClick={addExpense} className="finance-button">Add Expense</button>
                        <ul>
                            {expenses.map((expense, index) => (
                                <li key={index}>
                                    {expense.category}: £{expense.amount}
                                    <button onClick={() => editExpense(index)} className="edit-button">Edit</button>
                                    <button onClick={() => removeExpense(index)} className="remove-button">Remove</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
                <div className="finance-overview">
                    <h3>Total Income: £{totalIncome}</h3>
                    <h3>Total Expenses: £{totalExpenses}</h3>
                    <h3>Balance: £{balance}</h3>
                    <h3>Financial Overview</h3>
                    <Bar data={data} />
                </div>
            </div>
        </div>
    );
};

export default PersonalFinance;
