import React, { useState } from 'react';

const PersonalFinance = () => {
    const [incomeCategories, setIncomeCategories] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [newIncome, setNewIncome] = useState('');
    const [newExpense, setNewExpense] = useState('');
    const [weeklyEarnings, setWeeklyEarnings] = useState(0);

    const addIncomeCategory = () => {
        if (newIncome) {
            setIncomeCategories([...incomeCategories, newIncome]);
            setNewIncome('');
        }
    };

    const addExpense = () => {
        if (newExpense) {
            setExpenses([...expenses, newExpense]);
            setNewExpense('');
        }
    };

    const totalIncome = incomeCategories.length > 0 ? incomeCategories.length * weeklyEarnings : 0;
    const totalExpenses = expenses.reduce((acc, curr) => acc + parseFloat(curr), 0);
    const balance = totalIncome - totalExpenses;

    return (
        <div className="personal-finance">
            <h2>Personal Finance Management</h2>
            <div>
                <h3>Income Categories</h3>
                <input 
                    type="text" 
                    value={newIncome} 
                    onChange={(e) => setNewIncome(e.target.value)} 
                    placeholder="Add Income Category" 
                />
                <button onClick={addIncomeCategory}>Add</button>
                <ul>
                    {incomeCategories.map((category, index) => (
                        <li key={index}>{category}</li>
                    ))}
                </ul>
                <input 
                    type="number" 
                    value={weeklyEarnings} 
                    onChange={(e) => setWeeklyEarnings(e.target.value)} 
                    placeholder="Weekly Earnings" 
                />
            </div>
            <div>
                <h3>Expenses</h3>
                <input 
                    type="number" 
                    value={newExpense} 
                    onChange={(e) => setNewExpense(e.target.value)} 
                    placeholder="Add Expense" 
                />
                <button onClick={addExpense}>Add</button>
                <ul>
                    {expenses.map((expense, index) => (
                        <li key={index}>{expense}</li>
                    ))}
                </ul>
            </div>
            <div>
                <h3>Total Income: ${totalIncome}</h3>
                <h3>Total Expenses: ${totalExpenses}</h3>
                <h3>Balance: ${balance}</h3>
            </div>
        </div>
    );
};

export default PersonalFinance;
