import React, { useState } from 'react';

const BusinessFinance = () => {
    const [earnings, setEarnings] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [clientName, setClientName] = useState('');
    const [amount, setAmount] = useState('');
    const [isEarning, setIsEarning] = useState(true);

    const handleAddTransaction = () => {
        if (isEarning) {
            setEarnings([...earnings, { client: clientName, amount: parseFloat(amount) }]);
        } else {
            setExpenses([...expenses, { client: clientName, amount: parseFloat(amount) }]);
        }
        setClientName('');
        setAmount('');
    };

    const calculateROI = () => {
        const totalEarnings = earnings.reduce((acc, curr) => acc + curr.amount, 0);
        const totalExpenses = expenses.reduce((acc, curr) => acc + curr.amount, 0);
        return totalExpenses === 0 ? 'N/A' : ((totalEarnings - totalExpenses) / totalExpenses * 100).toFixed(2) + '%';
    };

    return (
        <div className="business-finance">
            <h2>Business Finance Management</h2>
            <div>
                <input
                    type="text"
                    placeholder="Client Name"
                    value={clientName}
                    onChange={(e) => setClientName(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                />
                <select onChange={(e) => setIsEarning(e.target.value === 'earning')}>
                    <option value="earning">Earning</option>
                    <option value="expense">Expense</option>
                </select>
                <button onClick={handleAddTransaction}>Add Transaction</button>
            </div>
            <h3>ROI: {calculateROI()}</h3>
            <h4>Earnings</h4>
            <ul>
                {earnings.map((earning, index) => (
                    <li key={index}>{earning.client}: ${earning.amount}</li>
                ))}
            </ul>
            <h4>Expenses</h4>
            <ul>
                {expenses.map((expense, index) => (
                    <li key={index}>{expense.client}: ${expense.amount}</li>
                ))}
            </ul>
        </div>
    );
};

export default BusinessFinance;
